mapper.delete:
DELETE FROM MN_BK_WISH
WHERE USER_ID = #{userId}


        AND ISBN = #{isbn}


        AND ISBN13 = #{isbn13}


        AND 1 = 0

DELETE FROM MN_MV_WISH
WHERE USER_ID = #{userId}

    AND MV_ID = #{mvId}

mapper.select:
SELECT COUNT(*)
FROM MN_MV_WISH w
WHERE w.MV_ID = #{mvId}

SELECT COUNT(*)
FROM MN_MV_WISH w
WHERE w.USER_ID = #{userId}
AND w.MV_ID = #{mvId}
